ZZ stringToNumber(string str)
{
    ZZ number,diez;
    int temp;
    long j=str.length()-1;
    diez=10;
    for(int i = 0; i < str.length(); i++)
    {
        temp=str[i]-48;
        number+=temp*power(diez,j-i);
    }

    return number;
}

ZZ modulo_ZZ(ZZ a, ZZ b)
{
    ZZ mod = a - b * (a/b);
    if(mod >= 0)
    {
        return mod;
    }
    mod = mod + b;
    return mod;
}
ZZ modulo(ZZ a, int b)
{
    ZZ mod = a - b * (a/b);
    if(mod >= 0)
    {
        return mod;
    }
    mod = mod + b;
    return mod;
}

